import https from "../https";

export default {
  name: "HelloWorld",
  data() {
    return {
      modal_loading: false,
      modal2: false,
      modalIndex2: 0,
      total: 0,
      loading: false,
      selectedI: "管理员",
      columns1: [
        {
          title: "姓名",
          key: "name"
        },
        {
          title: "邮箱",
          key: "email"
        },
        {
          title: "Action",
          key: "action",
          width: 150,
          align: "center",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.show(params.index);
                    }
                  }
                },
                "分配权限"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "error",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      this.remove(params.index);
                    }
                  }
                },
                "删除"
              )
            ]);
          }
        }
      ],
      data1: []
    };
  },
  methods: {
    show(index) {
      this.$Modal.confirm({
        okText: "确定",
        title: "选择权限",
        onOk: () => {
          let params = {
            select: this.selectedI
          };
          let headers = {
            Authorization: localStorage.getItem("Token")
          };
          try {
            https
              .fetchGet(
                `/api/admins/addAdmins/${this.data1[index]._id}`,
                params,
                headers
              )
              .then(data => {
                // 验证码结束,不正确
                if (data == "oauthError") {
                  this.$router.push({ path: "/login" });
                } else {
                  if (
                    data.data.msg == "Existing" ||
                    data.data.msg == "Success"
                  ) {
                    this.$Message.success("分配权限完毕");
                    this.showTotal();
                    this.showPage(0);
                  } else if (data.data.msg == "No permissions") {
                    this.$Message.error("你没有此权限");
                    return;
                  } else {
                    this.$Message.error("未知错误");
                    return;
                  }
                }
              })
              .catch(err => {
                console.log(err);
              });
          } catch (err) {
            console.log(err);
          }
        },
        render: h => {
          return h("div", {}, [
            h("div", {}, [
              // 姓名
              h(
                "div",
                {
                  style: {
                    color: "#808695",
                    marginBottom: "5px"
                  }
                },
                [
                  h(
                    "div",
                    {
                      style: {
                        color: "#808695",
                        marginBottom: "5px"
                      }
                    },
                    "姓名"
                  ),
                  h("Input", {
                    props: {
                      value: this.value,
                      autofocus: true,
                      disabled: true,
                      placeholder: this.data1[index].name
                    },
                    on: {
                      input: val => {
                        this.value = val;
                        this.data1[index].name = val;
                      }
                    }
                  })
                ]
              ),
              // 邮箱
              h(
                "div",
                {
                  style: {
                    color: "#808695",
                    marginBottom: "5px"
                  }
                },
                [
                  h(
                    "div",
                    {
                      style: {
                        color: "#808695",
                        marginBottom: "5px"
                      }
                    },
                    "邮箱"
                  ),
                  h("Input", {
                    props: {
                      value: this.value,
                      autofocus: true,
                      disabled: true,
                      placeholder: this.data1[index].email
                    }
                  })
                ]
              ),
              // 权限
              h(
                "div",
                {
                  style: {
                    color: "#808695",
                    marginBottom: "5px"
                  }
                },
                [
                  h(
                    "div",
                    {
                      style: {
                        color: "#808695",
                        marginBottom: "5px"
                      }
                    },
                    "权限"
                  ),
                  h(
                    "Select",
                    {
                      props: {
                        vModel: "model6",
                        value: this.value
                      },
                      style: {
                        width: "200px"
                      },
                      on: {
                        "on-change": val => {
                          this.selectedI = val;
                        }
                      }
                    },
                    [
                      h(
                        "Option",
                        {
                          props: {
                            value: "管理员"
                          }
                        },
                        "管理员"
                      ),
                      h(
                        "Option",
                        {
                          props: {
                            value: "超级管理员"
                          }
                        },
                        "超级管理员"
                      )
                    ]
                  )
                ]
              )
            ])
          ]);
        }
      });
    },
    remove(index) {
      this.modal2 = true;
      this.modalIndex2 = index;
    },
    del(index) {
      this.modal_loading = true;
      setTimeout(() => {
        this.modal_loading = false;
        this.modal2 = false;
        this.data1.splice(index, 1);
        this.$Message.success("成功删除");
      }, 2000);
    },
    showPage(page) {
      let params = {};
      let headers = {
        Authorization: localStorage.getItem("Token")
      };
      https
        .fetchGet(`/api/admins/showAdmins/${page}`, params, headers)
        .then(data => {
          // 验证码结束,不正确
          if (data == "oauthError") {
            this.$router.push({ path: "/login" });
          }
          this.data1 = data.data;
          this.loading = false;
        })
        .catch(err => {
          console.log(err);
        });
    },
    showTotal() {
      let params = {};
      let headers = {
        Authorization: localStorage.getItem("Token")
      };
      https
        .fetchGet("/api/admins/showAdminNum", params, headers)
        .then(data => {
          this.total = data.data.len;
        })
        .catch(err => {
          console.log(err);
        });
    },
    pageChange(index) {
      console.log(index, "有数据");
      this.showPage(index - 1);
    }
  },
  created() {
    this.loading = true;
    let params = {};
    let headers = {
      Authorization: localStorage.getItem("Token")
    };
    try {
      this.showTotal();
      this.showPage(0);
    } catch (err) {
      console.log(err);
    }
  }
};
